import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import Webcam from "react-webcam";
import * as tf from "@tensorflow/tfjs-core";
import { Pose } from "@mediapipe/pose";

const ModelViewer = ({ modelUrl, position }) => {
  const { scene } = useGLTF(modelUrl);
  return <primitive object={scene} position={position} scale={[1.5, 1.5, 1.5]} />;
};

const ARTryOn = ({ selectedModel }) => {
  const webcamRef = useRef(null);
  const [poseData, setPoseData] = useState(null);

  useEffect(() => {
    const loadPoseDetection = async () => {
      const pose = new Pose({
        locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
      });

      pose.setOptions({
        modelComplexity: 1,
        smoothLandmarks: true,
        enableSegmentation: false,
        minDetectionConfidence: 0.6,
        minTrackingConfidence: 0.6,
      });

      pose.onResults((results) => {
        if (results.poseLandmarks) {
          const leftShoulder = results.poseLandmarks[11];
          const rightShoulder = results.poseLandmarks[12];

          if (leftShoulder && rightShoulder) {
            // Smooth out the movement
            const x = (leftShoulder.x + rightShoulder.x) / 2;
            const y = (leftShoulder.y + rightShoulder.y) / 2;

            // Adjust position for better fit
            setPoseData({
              x: (x - 0.5) * 3, // Centering correction
              y: (y - 0.5) * -2, // Inverting y-axis to match Three.js
              z: -2.5, // Bringing closer to camera
            });
          }
        }
      });

      const processFrame = async () => {
        if (webcamRef.current && webcamRef.current.video.readyState === 4) {
          await pose.send({ image: webcamRef.current.video });
        }
        requestAnimationFrame(processFrame);
      };

      await pose.initialize();
      processFrame();
    };

    loadPoseDetection();
  }, []);

  return (
    <div style={{ position: "relative", width: "100%", height: "500px" }}>
      {/* Webcam Feed */}
      <Webcam
        ref={webcamRef}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 1,
        }}
        mirrored
      />

      {/* 3D Outfit Model */}
      <Canvas style={{ position: "absolute", top: 0, left: 0, zIndex: 2 }}>
        <ambientLight intensity={1} />
        <directionalLight position={[2, 2, 2]} />
        <OrbitControls enableZoom={false} />
        {selectedModel && poseData && (
          <ModelViewer modelUrl={selectedModel} position={[poseData.x, poseData.y, poseData.z]} />
        )}
      </Canvas>
    </div>
  );
};

export default ARTryOn;
