import React, { useEffect, useRef } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";

const Model = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} scale={1.5} />;
};

const ARTryOn = ({ selectedModel }) => {
  const videoRef = useRef(null);

  useEffect(() => {
    const startCamera = async () => {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ video: true });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (error) {
        console.error("Error accessing camera:", error);
      }
    };

    startCamera();
  }, []);

  return (
    <div>
      <h2>AR Try-On</h2>
      <video ref={videoRef} autoPlay playsInline width="400" height="300" style={{ position: "absolute", zIndex: -1 }} />
      <Canvas style={{ width: "400px", height: "300px" }}>
        <ambientLight intensity={1} />
        <OrbitControls />
        {selectedModel && <Model modelPath={selectedModel} />}
      </Canvas>
    </div>
  );
};

export default ARTryOn;
