import React, { useRef, useState, useEffect } from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, useGLTF } from "@react-three/drei";
import Webcam from "react-webcam";
import { Pose } from "@mediapipe/pose";

const ModelViewer = ({ modelUrl, position, scale }) => {
  const { scene } = useGLTF(modelUrl);
  return <primitive object={scene} position={position} scale={scale} />;
};

const ARTryOn = ({ selectedModel }) => {
  const webcamRef = useRef(null);
  const cameraRef = useRef(null); // Reference to the camera
  const [poseData, setPoseData] = useState({ x: 0, y: 0, z: -2.5, scale: 1 });

  useEffect(() => {
    const loadPoseDetection = async () => {
      const pose = new Pose({
        locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose/${file}`,
      });

      pose.setOptions({
        modelComplexity: 2,
        smoothLandmarks: true,
        enableSegmentation: false,
        minDetectionConfidence: 0.8,
        minTrackingConfidence: 0.8,
      });

      pose.onResults((results) => {
        if (results.poseLandmarks) {
          const leftShoulder = results.poseLandmarks[11]; // Left Shoulder
          const rightShoulder = results.poseLandmarks[12]; // Right Shoulder
          const leftHip = results.poseLandmarks[23]; // Left Hip
          const rightHip = results.poseLandmarks[24]; // Right Hip

          if (leftShoulder && rightShoulder && leftHip && rightHip) {
            const x = (leftShoulder.x + rightShoulder.x) / 2;
            const y = (leftShoulder.y + rightShoulder.y) / 2;
            const z = -2.0; // Adjusted for realistic depth

            const shoulderWidth = Math.abs(rightShoulder.x - leftShoulder.x);
            const hipWidth = Math.abs(rightHip.x - leftHip.x);
            const avgWidth = (shoulderWidth + hipWidth) / 2;
            const scale = Math.max(1.5, avgWidth * 12);

            setPoseData((prev) => ({
              x: prev.x * 0.8 + (x - 0.5) * 3 * 0.2,
              y: prev.y * 0.8 + (0.5 - y) * 2.5 * 0.2,
              z,
              scale,
            }));

            // Adjust camera position based on the user’s body position
            if (cameraRef.current) {
              cameraRef.current.position.set(x * 10, y * 10, z + 3); // Adjusting camera's position
            }
          }
        }
      });

      const processFrame = async () => {
        if (webcamRef.current && webcamRef.current.video.readyState === 4) {
          await pose.send({ image: webcamRef.current.video });
        }
        requestAnimationFrame(processFrame);
      };

      await pose.initialize();
      processFrame();
    };

    loadPoseDetection();
  }, []);

  return (
    <div style={{ position: "relative", width: "100%", height: "500px" }}>
      {/* Webcam Feed */}
      <Webcam
        ref={webcamRef}
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          zIndex: 1,
        }}
        mirrored
      />

      {/* 3D Outfit Model and Camera */}
      <Canvas
        style={{ position: "absolute", top: 0, left: 0, zIndex: 2 }}
        camera={{ position: [0, 0, 5], fov: 75, near: 0.1, far: 1000 }} // Set the initial camera position and FOV
      >
        <ambientLight intensity={0.8} />
        <directionalLight position={[2, 2, 2]} intensity={1} />
        <OrbitControls enableZoom={false} ref={cameraRef} />
        {selectedModel && (
          <ModelViewer
            modelUrl={selectedModel}
            position={[poseData.x, poseData.y, poseData.z]}
            scale={[poseData.scale, poseData.scale, poseData.scale]}
          />
        )}
      </Canvas>
    </div>
  );
};

export default ARTryOn;
