import React, { useState, useEffect, useRef } from "react";
import * as THREE from "three";
import { Canvas } from "@react-three/fiber";
import { useGLTF } from "@react-three/drei";
import { GLTFLoader } from "three/examples/jsm/loaders/GLTFLoader";
import * as mpPose from "@mediapipe/pose";
import "@mediapipe/pose/pose.css"; // This is not needed anymore, but you can keep it if the previous step fails

const Model = ({ url }) => {
  const { scene } = useGLTF(url);
  return <primitive object={scene} />;
};

const ARTryOn = ({ selectedModel }) => {
  const [poseData, setPoseData] = useState(null);
  const [modelUrl, setModelUrl] = useState(selectedModel);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  const videoWidth = 640;
  const videoHeight = 480;

  const [model, setModel] = useState(null);

  const loadModel = (url) => {
    const loader = new GLTFLoader();
    loader.load(url, (gltf) => {
      setModel(gltf.scene);
    });
  };

  useEffect(() => {
    if (selectedModel) {
      loadModel(selectedModel);
    }
  }, [selectedModel]);

  // Setup MediaPipe Pose
  useEffect(() => {
    const videoElement = videoRef.current;
    const pose = new mpPose.Pose({
      locateFile: (file) => `https://cdn.jsdelivr.net/npm/@mediapipe/pose@${mpPose.VERSION}/${file}`,
    });

    pose.onResults((results) => {
      if (results.poseLandmarks) {
        setPoseData(results.poseLandmarks);
      }
    });

    const camera = new window.VideoCapture(videoElement);
    const sendFrame = async () => {
      const frame = await camera.captureFrame();
      pose.send({ image: frame });
    };

    const startCamera = async () => {
      await navigator.mediaDevices.getUserMedia({
        video: true,
      }).then((stream) => {
        videoElement.srcObject = stream;
        videoElement.play();
        sendFrame();
      });
    };

    startCamera();

    return () => {
      if (videoElement.srcObject) {
        videoElement.srcObject.getTracks().forEach((track) => track.stop());
      }
    };
  }, []);

  const calculateScale = (poseData) => {
    if (!poseData) return 1;

    const shoulderWidth = Math.abs(poseData[11].x - poseData[12].x); // Left and right shoulder
    const chestHeight = Math.abs(poseData[23].y - poseData[24].y); // Upper body height
    const scale = shoulderWidth * chestHeight * 2; // Adjust scaling factor based on pose

    return scale;
  };

  const scaleOutfit = () => {
    if (model && poseData) {
      const scale = calculateScale(poseData);
      model.scale.set(scale, scale, scale);
    }
  };

  useEffect(() => {
    scaleOutfit();
  }, [poseData, model]);

  return (
    <div style={{ position: "relative", width: "100%", height: "100vh" }}>
      <video
        ref={videoRef}
        style={{ position: "absolute", top: "0", left: "0", zIndex: "1", width: "100%" }}
        width={videoWidth}
        height={videoHeight}
        autoPlay
        muted
      />
      <Canvas style={{ width: "100%", height: "100%", position: "absolute" }} gl={{ antialias: true }}>
        <ambientLight />
        <spotLight position={[10, 10, 10]} />
        {model && <primitive object={model} />}
      </Canvas>
    </div>
  );
};

export default ARTryOn;
